    private void btnCompleteDepositActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnCompleteDepositActionPerformed
    {//GEN-HEADEREND:event_btnCompleteDepositActionPerformed
        /**
         * set  final frame visible
         * insert data into transaction table
         */
        int accountNumber = Integer.parseInt(txfAccountNumber.getText());
        double amount = Double.parseDouble(txfDepositAmount.getText());
        Transactions transactions = new Transactions();
        transactions.deposit(accountNumber, "Deposit", amount);
        
        DepositGUI.setVisible(false);
        ThankYouGUI.setSize(WIDTH, HEIGHT);
        ThankYouGUI.setVisible(true);

        
        
    }//GEN-LAST:event_btnCompleteDepositActionPerformed

