    private void btnWithdraw2ActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_btnWithdraw2ActionPerformed
    {//GEN-HEADEREND:event_btnWithdraw2ActionPerformed
        /**
         * set  final frame visible
         * insert data into transaction table
         */
        
        Double amount = Double.parseDouble(txfOwnAmount.getText());
        
        if (amount >= 50)   {
            Transactions transactions = new Transactions();
            transactions.withdrawal(123456789, "Withdrawal", amount);
        
            WithdrawalGUI.setVisible(false);
            ThankYouGUI.setSize(WIDTH, HEIGHT);
            ThankYouGUI.setVisible(true);
        
        }   
        else if (amount < 50)   {
            
            String msg = "cannot withdraw amount less than R50";
            lblErrorMessage.setText(msg);
            lblErrorMessage.setVisible(true);
            
        }
        Transactions transactions = new Transactions();
        transactions.withdrawal(123456789, "Withdrawal", amount);
        
        WithdrawalGUI.setVisible(false);
        ThankYouGUI.setSize(WIDTH, HEIGHT);
        ThankYouGUI.setVisible(true);
        
        
    }//GEN-LAST:event_btnWithdraw2ActionPerformed

